mapper.select:
select
id, daily_id, daily_task_type, remarks, finish_date, create_by, create_date, update_by, update_date, sort
from txdata.t_daily_arrange


        and id = #{id}


        and daily_id = #{dailyId}


        and daily_task_type = #{dailyTaskType}


        and remarks = #{remarks}


        and finish_date = #{finishDate}


        and create_by = #{createBy}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_date = #{updateDate}


        and sort = #{sort}
