mapper.select:
select
id, daily_id, project_id, daily_task_type, usetime, percent_complete, remarks, create_by, create_date,
update_by, update_date, sort
from txdata.t_daily_task


        and id = #{id}


        and daily_id = #{dailyId}


        and project_id = #{projectId}


        and daily_task_type = #{dailyTaskType}


        and usetime = #{usetime}


        and percent_complete = #{percentComplete}


        and remarks = #{remarks}


        and create_by = #{createBy}


        and create_date = #{createDate}


        and update_by = #{updateBy}


        and update_date = #{updateDate}


        and sort = #{sort}
