mapper.select:
SELECT

FROM t_daily a

WHERE a.id = #{value}

SELECT

FROM t_daily a



		AND a.id = #{entity.id}


		AND a.title = #{entity.title}


		AND a.write_user = #{entity.writeUser}


		AND a.write_date = #{entity.writeDate}


		AND a.create_by = #{entity.createBy}


		AND a.Post = #{entity.post}


		AND a.create_date = #{entity.createDate}


		AND a.update_by = #{entity.updateBy}


		AND a.update_date = #{entity.updateDate}


		AND a.status = #{entity.status}


		AND a.report_date = #{entity.reportDate}


		AND a.remarks = #{entity.remarks}


		AND a.del_flag = #{entity.delFlag}


		AND a.del_flag = '0'


		AND a.file_path = #{entity.filePath}


ORDER BY a.update_date DESC,a.id DESC

mapper.delete:
DELETE FROM t_daily
WHERE id IN

	#{id}

